              <div key={index} className="group relative">
                <div className="bg-muted aspect-square overflow-hidden rounded-lg border">
                  <img
                    src={url}
                    alt={`Preview ${index + 1}`}
                    className="h-full w-full object-cover"
                    onError={(e) => {
